	/**
	 * Merge sort
	 * 
	 * start 
	 * 
	 * end ()list.size()-1
	 */
	public static void mergeSort(List<Double> list,int start,int end){
		
		//
		if(start>=end)
			return;
		
		int middle = (int) Math.floor((end-start)/2);
		middle+=start;
		
		//merge left
		mergeSort(list,start,middle);
		//merge right
		mergeSort(list,middle+1,end);
		
		int i = start;
		int j = middle+1;
		
		//
		//i<=ji ii+1
		//i   >jjii+1jj+1
		//ji
		//i==jj>end
		
		while(i<j && j<=end){
			
			if(list.get(i)<=list.get(j)){
				i++;
			}else{
				double t = list.get(j);
				list.remove(j);
				list.add(i, t);
				i++;j++;
			}
			
		}
		System.out.println(start+":"+end);
		for(double d : list){
			System.out.print(d+" ");
		}
		System.out.println();
	}
	
	
	
